<?php
/**
 * Functions which enhance the theme by hooking into WordPress.
 *
 * @package Aeon
 */

if ( ! defined( 'ABSPATH' ) ) {
	exit; // Exit if accessed directly.
}

if ( ! function_exists( 'aeon_body_classes' ) ) {
	/**
	 * Adds custom classes to the array of body classes.
	 *
	 * @param array $classes Classes for the body element.
	 * @return array
	 *
	 * @since 1.0.0
	 */
	function aeon_body_classes( $classes ) {
		$layout = aeon_get_layout();

		// Value have default, but we like to be extra careful.
		$classes[] = ( $layout ) ? $layout : 'no-sidebar';

		// Adds a class of hfeed to non-singular pages.
		if ( ! is_singular() ) {
			$classes[] = 'hfeed';
		}

		// Adds a class of no-sidebar when there is no sidebar present.
		if ( ! is_active_sidebar( 'aeon-sidebar' ) ) {
			$classes[] = 'no-sidebar';
		}

		// Adds a class if transparent header.
		if ( true === aeon_get_option( 'transparent_header' ) ) {
			$classes[] = 'has-transparent-header';
		}

		return $classes;
	}
	add_filter( 'body_class', 'aeon_body_classes' );
}

if ( ! function_exists( 'aeon_pingback_header' ) ) {
	/**
	 * Add a pingback url auto-discovery header for single posts, pages, or attachments.
	 *
	 * @since 1.0.0
	 */
	function aeon_pingback_header() {
		if ( is_singular() && pings_open() ) {
			printf( '<link rel="pingback" href="%s">', esc_url( get_bloginfo( 'pingback_url' ) ) );
		}
	}
	add_action( 'wp_head', 'aeon_pingback_header' );
}

if ( ! function_exists( 'aeon_get_media_query' ) ) {
	/**
	 * Get our media queries.
	 *
	 * @param string $name Name of the media query.
	 * @return string The full media query.
	 *
	 * @since 1.0.0
	 */
	function aeon_get_media_query( $name ) {
		$desktop = apply_filters( 'aeon_desktop_media_query', '(min-width:1025px)' );
		$tablet = apply_filters( 'aeon_tablet_media_query', '(min-width: 769px) and (max-width: 1024px)' );
		$mobile = apply_filters( 'aeon_mobile_media_query', '(max-width:768px)' );
		$mobile_menu = apply_filters( 'aeon_mobile_menu_media_query', $mobile );

		$queries = apply_filters(
			'aeon_media_queries',
			array(
				'desktop' => $desktop,
				'tablet' => $tablet,
				'mobile' => $mobile,
				'mobile-menu' => $mobile_menu,
			)
		);

		return $queries[ $name ];
	}
}

if ( ! function_exists( 'aeon_get_microdata' ) ) {
	/**
	 * Get any necessary microdata.
	 *
	 * @param string $context The element to target.
	 * @return string Our final attribute to add to the element.
	 *
	 * @since 1.0.0
	 */
	function aeon_get_microdata( $context ) {
		$data = false;

		if ( 'body' === $context ) {
			$type = 'WebPage';

			if ( is_home() || is_archive() || is_attachment() || is_tax() || is_single() ) {
				$type = 'Blog';
			}

			if ( is_search() ) {
				$type = 'SearchResultsPage';
			}

			$type = apply_filters( 'aeon_body_itemtype', $type );

			$data = sprintf(
				'itemtype="https://schema.org/%s" itemscope',
				esc_html( $type )
			);
		}

		if ( 'header' === $context ) {
			$data = 'itemtype="https://schema.org/WPHeader" itemscope';
		}

		if ( 'navigation' === $context ) {
			$data = 'itemtype="https://schema.org/SiteNavigationElement" itemscope';
		}

		if ( 'article' === $context ) {
			$type = apply_filters( 'aeon_article_itemtype', 'CreativeWork' );

			$data = sprintf(
				'itemtype="https://schema.org/%s" itemscope',
				esc_html( $type )
			);
		}

		if ( 'headline' === $context ) {
			$data = 'itemprop="headline"';
		}

		if ( 'description' === $context ) {
			$data = 'itemprop="description"';
		}

		if ( 'name' === $context ) {
			$data = 'itemprop="name"';
		}

		if ( 'url' === $context ) {
			$data = 'itemprop="url"';
		}

		if ( 'text' === $context ) {
			$data = 'itemprop="text"';
		}

		if ( 'post-author' === $context ) {
			$data = 'itemprop="author" itemtype="https://schema.org/Person" itemscope';
		}

		if ( 'comment-body' === $context ) {
			$data = 'itemtype="https://schema.org/Comment" itemscope';
		}

		if ( 'comment-author' === $context ) {
			$data = 'itemprop="author" itemtype="https://schema.org/Person" itemscope';
		}

		if ( 'sidebar' === $context ) {
			$data = 'itemtype="https://schema.org/WPSideBar" itemscope';
		}

		if ( 'footer' === $context ) {
			$data = 'itemtype="https://schema.org/WPFooter" itemscope';
		}

		if ( $data ) {
			return apply_filters( "aeon_{$context}_microdata", $data );
		}
	}
}

if ( ! function_exists( 'aeon_do_microdata' ) ) {
	/**
	 * Output our microdata for an element.
	 *
	 * @param string $context The element to target.
	 *
	 * @since 1.0.0
	 */
	function aeon_do_microdata( $context ) {
		echo aeon_get_microdata( $context ); // phpcs:ignore WordPress.Security.EscapeOutput.OutputNotEscaped -- Escaped in function.
	}
}

