if ( ! function_exists( 'aeon_show_excerpt' ) ) {
	/**
	 * Figure out if we should show the blog excerpts or full posts.
	 *
	 * @since 1.0.0
	 */
	function aeon_show_excerpt() {
		global $post;

		// Check to see if the more tag is being used.
		$more_tag = apply_filters( 'aeon_more_tag', strpos( $post->post_content, '<!--more-->' ) );

		$format = ( false !== get_post_format() ) ? get_post_format() : 'standard';

		if ( class_exists( 'Aeon_Blog' ) && true === aeon_get_option( 'archive_type' ) ) {
			$show_excerpt = false;
		} else {
			$show_excerpt = true;
		}

		$show_excerpt = ( 'standard' !== $format ) ? false : $show_excerpt;

		$show_excerpt = ( $more_tag ) ? false : $show_excerpt;

		return apply_filters( 'aeon_show_excerpt', $show_excerpt );
	}
}

if ( ! function_exists( 'aeon_excerpt_more' ) ) {
	/**
	 * Prints the read more HTML to post excerpts.
