	 * @since 1.0.0
	 *
	 * @param string $more The string shown within the more link.
	 * @return string The HTML for the more link.
	 */
	function aeon_excerpt_more( $more ) {
		$read_more_text    = apply_filters( 'aeon_post_read_more', esc_html__( 'Read More &raquo;', 'aeonwp' ) );
		$read_more_classes = apply_filters( 'aeon_post_read_more_class', array( 'read-more-link' ) );

		$output = ' &hellip;<p class="read-more"><a class="' . esc_attr( implode( ' ', $read_more_classes ) ) . '" href="' . esc_url( get_permalink() ) . '"> ' . the_title( '<span class="screen-reader-text">', '</span>', false ) . $read_more_text . '</a></p>';

		return apply_filters( 'aeon_excerpt_more_output', $output );
	}
	add_filter( 'excerpt_more', 'aeon_excerpt_more' );
}

