if ( ! function_exists( 'aeon_post_structure' ) ) {

	/**
	 * Get post structure
	 *
	 * @return html Markup.
	 */
	function aeon_post_structure() {
		$structure = aeon_get_option( 'archive_structure' );
		if ( is_array( $structure ) ) {
			foreach ( $structure as $order ) { // phpcs:ignore WordPress.WP.GlobalVariablesOverride.Prohibited
				switch ( $order ) {
					// Featured Image.
					case 'image':
						aeon_post_thumbnail();
						break;

					// Title and Meta.
					case 'title-meta':
						aeon_post_heading();
						break;
				}
			}
		}
	}
}

