		$output .= aeon_get_svg_icon( 'meta-author' );
		$output .= '<a class="url fn n" href="' . esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) ) . '" title="' . esc_attr( sprintf( esc_html__( 'View all posts by %s', 'aeonwp' ), get_the_author() ) ) . '" rel="author" itemprop="url">' . esc_html( get_the_author() ) . '</a>';
		$output .= '</li>';
		return apply_filters( 'aeon_post_author_output', $output );
	}
}

