if ( ! function_exists( 'aeon_post_meta' ) ) {
	/**
	 * Post meta.
	 *
	 * @param  string $post_meta Post meta.
	 * @return string post meta markup.
	 */
	function aeon_post_meta( $post_meta ) {
		$output = '';
		$loop_count = 1;

		foreach ( $post_meta as $meta_value ) {
			switch ( $meta_value ) {
				case 'author':
					$author = get_the_author();
					if ( ! empty( $author ) ) {
						$output .= aeon_post_author();
					}
					break;
				case 'date':
					$output .= aeon_post_date();
					break;
				case 'cat':
					$category = aeon_post_cat();
					if ( '' !== $category ) {
						$output .= $category;
					}
					break;
				case 'comments':
					$comment = aeon_post_comments();
					if ( '' !== $comment ) {
						$output .= $comment;
					}
					break;
				default:
					$output = apply_filters( 'aeon_meta_case_' . $meta_value, $output, $loop_count );
			}
			$loop_count ++;
		}

		return $output;
	}
}

if ( ! function_exists( 'aeon_get_post_meta' ) ) {

	/**
	 * Prints HTML with meta information.
	 *
	 * @param boolean $echo   Output print or return.
	 * @return string|void
	 */
	function aeon_get_post_meta( $echo = true ) {
		$enable_meta       = apply_filters( 'aeon_post_meta_enabled', '__return_true' );
		$post_meta         = aeon_get_option( 'archive_meta' );
		$current_post_type = get_post_type();
		$post_type_array   = apply_filters( 'aeon_post_type_meta', array( 'post' ) );

		$output = '';
		if ( is_array( $post_meta ) && ( in_array( $current_post_type, $post_type_array ) || 'attachment' == $current_post_type ) && $enable_meta ) {
			$output_str = aeon_post_meta( $post_meta );
			if ( ! empty( $output_str ) ) {
				$output = apply_filters( 'aeon_post_meta', '<div class="entry-meta"><ul>' . $output_str . '</ul></div>', $output_str ); // WPCS: XSS OK.
			}
		}
		if ( $echo ) {
			echo $output; // phpcs:ignore WordPress.Security.EscapeOutput.OutputNotEscaped
		} else {
			return $output;
		}
	}
}

