if ( ! function_exists( 'aeon_single_post_nav' ) ) {
	/**
	 * Get Post Navigation.
	 *
	 * Checks post navigation, if exists return as button.
	 *
	 * @since 1.0.0
	 *
	 * @return mixed Post Navigation Buttons.
	 */
	function aeon_single_post_nav() {

		/* translators: used to get the icon */
		$nav_next = esc_html__( 'Next %s', 'aeonwp' ) . aeon_get_svg_icon( 'nav-right-arrow' );
		/* translators: used to get the icon */
		$nav_prev = aeon_get_svg_icon( 'nav-left-arrow' ) . esc_html__( 'Previous %s', 'aeonwp' );

		if ( is_rtl() ) {
			/* translators: used to get the icon */
			$nav_next = esc_html__( 'Next %s', 'aeonwp' ) . aeon_get_svg_icon( 'nav-left-arrow' );
			/* translators: used to get the icon */
			$nav_prev = aeon_get_svg_icon( 'nav-right-arrow' ) . esc_html__( 'Previous %s', 'aeonwp' );
