if ( ! function_exists( 'aeon_content_nav' ) ) {
	/**
	 * Display navigation to next/previous pages when applicable.
	 *
	 * @since 1.0.0
	 *
	 * @param string $nav_id The id of our navigation.
	 */
	function aeon_content_nav( $nav_id ) {
		global $wp_query, $post;

		// Do not print empty markup on single pages if there is nowhere to navigate.
		if ( is_single() ) {
			$previous = ( is_attachment() ) ? get_post( $post->post_parent ) : get_adjacent_post( false, '', true );
			$next = get_adjacent_post( false, '', false );

			if ( ! $next && ! $previous ) {
				return;
			}
		}

		// Do not print empty markup in archives if there is only one page.
		if ( $wp_query->max_num_pages < 2 && ( is_home() || is_archive() || is_search() ) ) {
			return;
		}
		?>
		<nav id="<?php echo esc_attr( $nav_id ); ?>" class="paging-navigation">
			<span class="screen-reader-text"><?php esc_html_e( 'Post navigation', 'aeonwp' ); ?></span>

			<?php
			if ( is_home() || is_archive() || is_search() ) : // navigation links for home, archive, and search pages.

				if ( function_exists( 'the_posts_pagination' ) ) {
					the_posts_pagination(
						array(
							'mid_size' => apply_filters( 'aeon_pagination_mid_size', 1 ),
							'prev_text' => apply_filters(
								'aeon_previous_link_text',
								sprintf(
									/* translators: left arrow */
									__( '%s Previous', 'aeonwp' ),
									'<span aria-hidden="true">&larr;</span>'
								)
							),
							'next_text' => apply_filters(
								'aeon_next_link_text',
								sprintf(
									/* translators: right arrow */
									__( 'Next %s', 'aeonwp' ),
									'<span aria-hidden="true">&rarr;</span>'
								)
							),
							'before_page_number' => sprintf(
								'<span class="screen-reader-text">%s</span>',
								_x( 'Page', 'prepends the pagination page number for screen readers', 'aeonwp' )
							),
						)
					);
				}
			endif;
			?>
		</nav>
		<?php
	}
}

/**
 * Add our post navigation after post loops.
