 * @since 1.0.0
 * @param string $template The template of the current action.
 */
function aeon_do_post_navigation( $template ) {
	$templates = apply_filters(
		'aeon_post_navigation_templates',
		array(
			'index',
			'archive',
			'search',
		)
	);

	if ( in_array( $template, $templates ) && apply_filters( 'aeon_show_post_navigation', true ) ) {
		echo aeon_content_nav( 'ae-pagination' ); // phpcs:ignore WordPress.Security.EscapeOutput.OutputNotEscaped
	}
}
