	 *
	 * @since 1.0.0
	 */
	function aeon_register_metabox() {
		if ( ! current_user_can( 'edit_theme_options' ) ) {
			return;
		}

		global $post;

		$blog_id = get_option( 'page_for_posts' );

		// No need for the Layout metabox on the blog page.
		if ( $blog_id && (int) $blog_id === (int) $post->ID ) {
			return;
		}

		$post_types = get_post_types( array( 'public' => true ) );

		foreach ( $post_types as $type ) {
			if ( 'attachment' !== $type ) {
				add_meta_box(
					'aeon_options_metabox',
					apply_filters( 'aeon_metabox_title', esc_html__( 'Aeon', 'aeonwp' ) ) . ' ' . esc_html__( 'Settings', 'aeonwp' ),
					'aeon_do_metabox',
					$type,
					'side'
				);
			}
		}
	}
	add_action( 'add_meta_boxes', 'aeon_register_metabox' );
}

