if ( ! function_exists( 'aeon_save_metadata' ) ) {
	/**
	 * Saves the sidebar metabox meta data.
	 *
	 * @param string $post_id The option name to look up.
	 *
	 * @since 1.0.0
	 */
	function aeon_save_metadata( $post_id ) {
		$is_autosave = wp_is_post_autosave( $post_id );
		$is_revision = wp_is_post_revision( $post_id );
		$is_valid_nonce = ( isset( $_POST['aeon_metabox_nonce'] ) && wp_verify_nonce( sanitize_key( $_POST['aeon_metabox_nonce'] ), basename( __FILE__ ) ) ) ? true : false;

		if ( $is_autosave || $is_revision || ! $is_valid_nonce ) {
			return;
		}

		if ( ! current_user_can( 'edit_post', $post_id ) ) {
			return $post_id;
		}

		$sidebar_key   = 'aeon-layout-meta';
		$sidebar_value = filter_input( INPUT_POST, $sidebar_key, FILTER_SANITIZE_STRING );

		if ( $sidebar_value ) {
			update_post_meta( $post_id, $sidebar_key, $sidebar_value );
		} else {
			delete_post_meta( $post_id, $sidebar_key );
		}

		$disable_content_title_key   = 'aeon-disable-title';
		$disable_content_title_value = filter_input( INPUT_POST, $disable_content_title_key, FILTER_SANITIZE_STRING );

		if ( $disable_content_title_value ) {
			update_post_meta( $post_id, $disable_content_title_key, $disable_content_title_value );
		} else {
			delete_post_meta( $post_id, $disable_content_title_key );
		}

		do_action( 'aeon_metabox_save', $post_id );
	}
	add_action( 'save_post', 'aeon_save_metadata' );
}

