if ( ! function_exists( 'aeon_get_option' ) ) {
	/**
	 * A wrapper function to get our options.
	 *
	 * @param string $option The option name to look up.
	 * @return string The option value.
	 *
	 * @since 1.0.0
	 */
	function aeon_get_option( $option ) {
		$defaults = aeon_get_defaults();

		if ( ! isset( $defaults[ $option ] ) ) {
			return;
		}

		$options = wp_parse_args(
			get_option( 'aeon_settings', array() ),
			$defaults
		);

		return $options[ $option ];
	}
}

if ( ! function_exists( 'aeon_update_option' ) ) {
	/**
	 * Update Theme options.
	 *
	 * @param  string $option option key.
	 * @param  Mixed  $value  option value.
	 * @return void
	 */
	function aeon_update_option( $option, $value ) {
		do_action( "aeon_before_update_option_{$option}", $value, $option );

		// Get all customizer options.
		$options = get_option( 'aeon_settings' );

		// Update value in options array.
		$options[ $option ] = $value;

		update_option( 'aeon_settings', $options );

		do_action( "aeon_after_update_option_{$option}", $value, $option );
	}
}

if ( ! function_exists( 'aeon_delete_option' ) ) {
	/**
	 * Update Theme options.
	 *
	 * @param  string $option option key.
	 * @return void
	 */
	function aeon_delete_option( $option ) {
		do_action( "aeon_before_delete_option_{$option}", $option );

		// Get all customizer options.
		$options = get_option( 'aeon_settings' );

		// Update value in options array.
		unset( $options[ $option ] );

		update_option( 'aeon_settings', $options );

		do_action( "aeon_after_delete_option_{$option}", $option );
	}
}

if ( ! function_exists( 'aeon_get_layout' ) ) {
	/**
	 * Get the layout for the current page.
	 *
	 * @return string The sidebar layout location.
	 *
	 * @since 1.0.0
	 */
	function aeon_get_layout() {
		$layout = aeon_get_option( 'general_layout' );

		if ( is_home()
			|| is_archive()
			|| is_search()
			|| is_tax() ) {
			$layout = aeon_get_option( 'blog_layout' );
		}
		
		if ( is_404()
			|| is_singular( 'elementor_library' ) ) {
			$layout = 'no-sidebar';
		}
		
		if ( is_single() ) {
			$layout = aeon_get_option( 'single_post_layout' );
		}

		// LearnDash.
		if ( defined( 'LEARNDASH_VERSION' ) ) {
			$ld_pt = array(
				'quiz',
				'certificates',
				'lessons',
				'topic',
				'transactions',
				'essays',
				'assignment',
				'courses',
			);
			foreach ( $ld_pt as $name ) {
				if ( is_singular( 'sfwd-' . $name ) ) {
					$layout = aeon_get_option( 'sfwd_' . $name . '_layout' );
				}
			}
			if ( is_singular( 'ld-exam' ) ) {
				$layout = aeon_get_option( 'ld_exam_layout' );
			}
		}

		// LifterLMS.
		if ( class_exists( 'LifterLMS' ) ) {
			$llms_pt = array(
				'course',
				'lesson',
				'llms_quiz',
				'llms_membership',
				'llms_certificate',
				'llms_my_certificate',
			);
			foreach ( $llms_pt as $name ) {
				if ( is_singular( $name ) ) {
					$layout = aeon_get_option( $name . '_layout' );
				}
			}
		}
		
		// If Custom Post Type.
		$all_post_types = aeon_get_post_types_objects();
		foreach ( $all_post_types as $post_type_item ) {
			$post_type_name = $post_type_item->name;
			$ignore_type = aeon_get_post_types_to_ignore();

			if ( ! in_array( $post_type_name, $ignore_type, true ) ) {
				if ( is_archive( $post_type_name )
					|| is_tax( $post_type_name ) ) {
					$layout = aeon_get_option( $post_type_name . '_layout' );
				}

				if ( is_singular( $post_type_name ) ) {
					$layout = aeon_get_option( 'single_' . $post_type_name . '_layout' );
				}
			}
		}
		
		if ( is_singular() ) {
			$layout_meta = get_post_meta( get_the_ID(), 'aeon-layout-meta', true );

			if ( $layout_meta ) {
				$layout = $layout_meta;
			}
		}

		return apply_filters( 'aeon_sidebar_layout', $layout );
	}
}

if ( ! function_exists( 'aeon_display_sidebar' ) ) {
	/**
	 * Display sidebar.
	 *
	 * @since 1.0.0
	 */
	function aeon_display_sidebar() {
		// Return if full width or full screen.
		if ( in_array( aeon_get_layout(), array( 'full-width', 'no-sidebar' ) ) ) {
