	 */
	function aeon_get_post_types( $args = [] ) {
		$post_type_args = [
			'show_in_nav_menus' => true,
		];

		if ( ! empty( $args['post_type'] ) ) {
			$post_type_args['name'] = $args['post_type'];
		}

		$_post_types = get_post_types( $post_type_args, 'objects' );

		$post_types  = [];
		$post_types['any']  = esc_html__( 'Any', 'aeonwp' );

		foreach ( $_post_types as $post_type => $object ) {
			$post_types[ $post_type ] = $object->label;
		}

		return $post_types;
	}
}

/**
 * Get our CSS method.
 *
 * @since 1.0.0
 */
function aeon_get_css_method() {
	$mode = aeon_get_option( 'load_css_file' );

	if ( ( function_exists( 'is_customize_preview' ) && is_customize_preview() )
		|| is_preview()
		|| ( function_exists( 'is_amp_endpoint' ) && is_amp_endpoint() ) ) {
		return false;
	}

	if ( ! defined( 'AEON_VERSION' ) ) {
		return false;
	}

	return $mode;
}

/**
 * Get all public post types.
 *
 * @return array of post types.
 */
function aeon_get_post_types_objects() {
	$args = array(
		'public' => true,
		'_builtin' => false,
	);
	$output = 'objects'; // names or objects, note names is the default.
	$operator = 'and';
	$post_types = get_post_types( $args, $output, $operator );
	$post_types_objects = apply_filters( 'aeon_public_post_type_objects', $post_types );
	return $post_types_objects;
}

