if ( ! function_exists( 'aeon_get_premium_url' ) ) {
	/**
	 * Create a URL to the pro version.
	 * Allows the use of a referral ID and campaign.
	 *
	 * @since 1.0.0
	 *
	 * @param string $url URL to premium page.
	 * @param bool   $trailing_slash Whether we want to include a trailing slash.
	 * @return string The URL to atmostfear-entertainment.com.
	 */
	function aeon_get_premium_url( $url = 'https://www.atmostfear-entertainment.com/aeon/pro', $trailing_slash = true ) {
		if ( $trailing_slash ) {
			$url = trailingslashit( $url );
		}

		$args = apply_filters(
			'aeon_premium_url_args',
			array(
				'ref' => null,
				'campaign' => null,
			)
		);

		if ( isset( $args['ref'] ) ) {
			$url = add_query_arg( 'ref', absint( $args['ref'] ), $url );
		}

		if ( isset( $args['campaign'] ) ) {
			$url = add_query_arg( 'campaign', sanitize_text_field( $args['campaign'] ), $url );
		}

		return esc_url( $url );
	}
}
